  `use_time` datetime(0) NULL DEFAULT NULL COMMENT '使用时间',
  `order_sn` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '订单号码',
