INSERT INTO `config_info` VALUES (344, 'learn-shop-core-order.yml', 'DEFAULT_GROUP', 'spring:\n  cloud:\n    nacos:\n      discovery:\n        username: nacos\n        password: nacos\n        namespace: ${config.register.namespace} # 命名空间\n        server-addr: ${config.register.serverAddr} # 配置注册中心\n    loadbalancer:\n      retry:\n        enabled: true #开启重试机制\n  rabbitmq:\n    host: ${config.mq.host} #rabbitMQ服务器地址\n    port: ${config.mq.port}  #rabbitMQ服务器端口\n    username: ${config.mq.username}\n    password: ${config.mq.password}\n    virtual-host: ${config.mq.virtual-host}\n    # 配置消息重试\n    listener:\n      simple:\n        acknowledge-mode: auto  # 自动ack\n        retry:\n          enabled: true\n          max-attempts: 1\n          initial-interval: 1000  # 重试初始间隔时间\n          multiplier: 2 # 间隔时间乘子，间隔时间*乘子=下一次的间隔时间，最大不能超过设置的最大间隔时间\n          max-interval: 10000   # 重试最大间隔时间\nfeign: #远程连接\n  hystrix: #熔断器配置\n    enabled: true #启动熔断器\nhystrix:\n  command:\n    default:\n      execution:\n        timeout:\n          enabled: true #Hystrix 是否启用超时时间\n        isolation:\n          thread:\n            timeoutInMilliseconds: 8000 #Hystrix断路器的超时时间，默认是1s，断路器的超时时间需要大于ribbon的超时时间，不然不会触发重试\n          strategy: SEMAPHORE\n\nribbon:\n  ConnectTimeout: 1000 #ribbon请求连接的超时时间\n  ReadTimeout: 3000 #请求处理的超时时间\n  OkToRetryOnAllOperations: true #对所有请求操作都进行重试\n  MaxAutoRetries: 0 #对当前服务的重试次数（第一次分配给9082的时候，如果404，则再重试MaxAutoRetries次，如果还是404，则切换到其他服务MaxAutoRetriesNextServer决定）\n  MaxAutoRetriesNextServer: 5 #切换服务的次数(比如本次请求分配给9082处理，发现404，则切换分配给9081处理，如果还是404，则返回404给客户端）', 'aff3bb116e21251e76661567f6a7e6fe', '2021-03-12 07:25:26', '2022-03-27 07:53:20', 'nacos', '172.19.0.1', '', 'dev', '', '', '', 'yaml', '');
