INSERT INTO `config_info` VALUES (403, 'learn-shop-core-promotion.yml', 'DEFAULT_GROUP', 'spring:\n  cloud:\n    nacos:\n      discovery:\n        username: nacos\n        password: nacos\n        namespace: ${config.register.namespace} # 命名空间\n        server-addr: ${config.register.serverAddr} # 配置注册中心\n  rabbitmq:\n    host: ${config.mq.host} #rabbitMQ服务器地址\n    port: ${config.mq.port}  #rabbitMQ服务器端口\n    username: ${config.mq.username}\n    password: ${config.mq.password}\n    virtual-host: ${config.mq.virtual-host}\n    # 配置消息重试\n    listener:\n      simple:\n        acknowledge-mode: auto  # 自动ack\n        retry:\n          enabled: true\n          max-attempts: 5\n          initial-interval: 1000  # 重试初始间隔时间\n          multiplier: 2 # 间隔时间乘子，间隔时间*乘子=下一次的间隔时间，最大不能超过设置的最大间隔时间\n          max-interval: 10000   # 重试最大间隔时间\n\nfeign: #远程连接\n  hystrix: #熔断器配置\n    enabled: true #启动熔断器\n\n\nseckill:\n  gen-salt: wq<<.((0kkoe$$% # 设置盐值字符串，随便定义，用于混淆MD5值\n  load-data-start-before: 10 # 自动任务加载秒杀开始前多少分钟的数据加到缓存中（单位：分钟）\n  clear-data-end-arfer: 30 # 设置 redis 中秒杀活动结束后多少分钟数据过期（单位：分钟）\n  order-exp: 30 # 订单过期时间（单位：分钟）', 'd75881ee127581a0ee0844d9de24d4de', '2022-03-27 07:58:22', '2022-03-27 07:58:22', NULL, '172.19.0.1', '', 'dev', '', NULL, NULL, 'yaml', NULL);
